#!/bin/sh

echo "Usage: ./solve.sh <URL_RPC>"
PK="0x3da2b9f371d75f03e91bbbeb1da81fac34721d71a2b12bd1ae547426a4b4f559"
RPC_URL="$1"
BYTE_CODE_KEY=""

# Create forge env
forge init bybreizh
cp -r src/ bybreizh/
cp -r script/ bybreizh/

cd bybreizh

# Deploy key
cast send 0xad4967EA626502f0b8F89dc172F2BAa13397f1e2 "deploy(uint256,bytes32,bytes)" 0 0xad0a990189248f3b99ddf7978b28654f5261b05830d2b4356927726d9528810a "$BYTE_CODE_KEY" -r "$RPC_URL" --private-key "$PK" --value 0.1ether

# Run solve script
forge script script/Solve.s.sol --rpc-url "$RPC_URL" --private-key "$PK" --broadcast --evm-version cancun
